var bottomNav=document.querySelector("#mobileNav");window.addEventListener("scroll",function(){200<window.pageYOffset?bottomNav.style.transform="translateX(0)":bottomNav.style.transform="translateX(200%)"});for(var el=document.querySelectorAll("nav a"),i=0;i<el.length;i++)el[i].addEventListener("click",function(e){e.preventDefault();var t=e.srcElement.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth"})});